arguments.argument:
if(global.isHost && hp<=0) {
    sendEventPlayerDeath(player, lastDamageDealer, lastDamageSource);
    doEventPlayerDeath(player, lastDamageDealer, lastDamageSource);
    if(player.hat>0 and lastDamageDealer != -1 and lastDamageDealer != player and random(1)<0.25) {
        lastDamageDealer.hat = player.hat;
        player.hat = 0;
        GameServer.performHatUpdate=true;
    }
    with(GameServer) {
        ServerBalanceTeams();
    }
    exit;
}

if(hp>maxHp) {
    hp=maxHp;
}

if(((aimDirection+270) mod 360)>180) {
    image_xscale=1;
    currentWeapon.image_xscale=1;
    currentWeapon.image_angle = aimDirection;
} else {
    image_xscale=-1;
    currentWeapon.image_xscale=-1;
    currentWeapon.image_angle = aimDirection+180;
}

currentWeapon.x=round(x);
currentWeapon.y=round(y);

// Limit people to the area of the room to prevent the
// "Falling through the floors" issue.
if(x<0) {
    x=0;
}
if(x>room_width) {
    x = room_width
}
if(y<0) {
    y = 0;
}
if(y>room_height) {
    y = room_height;
}


// Cloak
if (cloak) {
    cloakAlpha = max(cloakAlpha - 0.05, 0);
} else {
    cloakAlpha = min(cloakAlpha + 0.05, 1);
}

// Taunts
if taunting == true{
    tauntindex += tauntspeed*0.1;
    if tauntindex >= tauntend taunting=false;
}

//sandvich
if omnomnomnom == true{
    omnomnomnomindex += 0.25;
    image_xscale=xscale;
    if hp <= maxHp hp += 0.4;
    if omnomnomnomindex >= omnomnomnomend omnomnomnom=false;
}

// Last x/y position for death cam if player is dead
    player.lastKnownx=x;
    player.lastKnowny=y;
