arguments.argument:
{
    if (readyToShoot && ammoCount >= 66) && owner.keyState & $08 {

     var shot;
        randomize();
        if !collision_line(x,y,x+lengthdir_x(28,owner.aimDirection),y+lengthdir_y(28,owner.aimDirection),Obstacle,1,0) and !place_meeting(x+lengthdir_x(25,owner.aimDirection),y+lengthdir_y(25,owner.aimDirection),TeamGate) {
            playsound(x,y,CompressionBlastSnd);
            shot = instance_create(x+lengthdir_x(25,owner.aimDirection),y+lengthdir_y(25,owner.aimDirection),Flare);
            shot.direction=owner.aimDirection;
            shot.speed=15;
            shot.owner=owner;
            shot.ownerPlayer=ownerPlayer;
            shot.team=owner.team;
            shot.weapon=WEAPON_FLARE;
            justShot=true;
            readyToShoot=false;
            ammoCount-=66;
            if ammoCount > 0 alarm[0]=refireTime+39;
            else alarm[0]=50;
        }
    }
    else if (readyToShoot && ammoCount >= 2) {
        if(alarm[3]<=0) {
            loopsoundstart(x,y,FlamethrowerSnd);
        } else {
            loopsoundmaintain(x,y,FlamethrowerSnd);
        }
        alarm[3]=2;

        var shot;
        randomize();
        if !collision_line(x,y,x+lengthdir_x(28,owner.aimDirection),y+lengthdir_y(28,owner.aimDirection),Obstacle,1,0) and !place_meeting(x+lengthdir_x(25,owner.aimDirection),y+lengthdir_y(25,owner.aimDirection),TeamGate) {
            shot = instance_create(x+lengthdir_x(25,owner.aimDirection),y+lengthdir_y(25,owner.aimDirection),Flame);
            shot.direction=owner.aimDirection+(random(10)-5);
            shot.speed=5+random(5);
            shot.owner=owner;
            shot.ownerPlayer=ownerPlayer;
            shot.team=owner.team;
            shot.weapon=WEAPON_FLAMETHROWER;
            with(shot) {
                motion_add(owner.direction, owner.speed);
            }
            justShot=true;
            readyToShoot=false;
            ammoCount-=2;
            if ammoCount > 0 alarm[0]=refireTime;
            else alarm[0]=50;
        }
    }
}
